import React, { useState, useEffect } from 'react';

function Partners({ language, content }) {
  const t = content[language];
  
  // Default partners
  const defaultPartners = [
    { 
      id: 1,
      nameEn: 'Management & Science University',
      nameAr: 'جامعة الإدارة والعلوم',
      logoPath: 'partner 1.jpeg',
      order: 1
    },
    { 
      id: 2,
      nameEn: 'UCSI University',
      nameAr: 'جامعة UCSI',
      logoPath: 'Partener 2.png',
      order: 2
    },
    { 
      id: 3,
      nameEn: 'ALQAWASMI',
      nameAr: 'القواسمي',
      logoPath: 'parnter 3.jpeg',
      order: 3
    },
    { 
      id: 4,
      nameEn: 'Duy Tân University',
      nameAr: 'جامعة دوي تان',
      logoPath: 'parnter 4.jpeg',
      order: 4
    },
    { 
      id: 5,
      nameEn: 'MK Elite',
      nameAr: 'إم كي إيليت',
      logoPath: 'partener 5.jpeg',
      order: 5
    },
  ];
  
  // Load partners from localStorage
  const [partners, setPartners] = useState(() => {
    try {
      const saved = localStorage.getItem('partners');
      if (saved) {
        const parsed = JSON.parse(saved);
        return parsed.sort((a, b) => a.order - b.order);
      }
    } catch (error) {
      console.error('Error loading partners:', error);
    }
    return defaultPartners;
  });
  
  // Reload partners when localStorage changes
  useEffect(() => {
    const loadPartners = () => {
      try {
        const saved = localStorage.getItem('partners');
        if (saved) {
          const parsed = JSON.parse(saved);
          setPartners(parsed.sort((a, b) => a.order - b.order));
        }
      } catch (error) {
        console.error('Error loading partners:', error);
      }
    };
    
    // Listen for storage events from other tabs
    const handleStorageChange = (e) => {
      if (e.key === 'partners') {
        loadPartners();
      }
    };
    
    // Listen for custom events from same window
    const handleCustomUpdate = () => {
      loadPartners();
    };
    
    window.addEventListener('storage', handleStorageChange);
    window.addEventListener('partnersUpdated', handleCustomUpdate);
    
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      window.removeEventListener('partnersUpdated', handleCustomUpdate);
    };
  }, []);

  return (
    <section className="partners page-section scroll-animate">
      <div className="container">
        <h2>{t.partners.title}</h2>
        <p className="partners-intro">{t.partners.intro}</p>
        
        <div className="partners-grid">
          {partners.map((partner) => (
            <div key={partner.id} className="partner-card">
              <div className="partner-logo">
                <img 
                  src={`${process.env.PUBLIC_URL}/${partner.logoPath}`} 
                  alt={language === 'ar' ? partner.nameAr : partner.nameEn} 
                />
              </div>
              <h3 className="partner-name">
                {language === 'ar' ? partner.nameAr : partner.nameEn}
              </h3>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Partners;
